import React, { useState } from 'react'
import './hotel.css'
import Navbar from '../../components/navbar/Navbar'
import Header from '../../components/header/Header'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLocationDot,faCircleArrowLeft,faCircleArrowRight,faCircleXmark } from '@fortawesome/free-solid-svg-icons'
import MailList from '../../components/mailList/MailList'
import Footer from '../../components/footer/Footer'

const Hotel = () => {

  const [slideNumer, setSlideNumber] = useState(0);
  const [slider, openSlider] = useState(false);

  const photos = [
    {
      src: "https://pix10.agoda.net/hotelImages/21979833/0/2e9b585afbdd49e58054892f86230798.jpg?ca=23&ce=0&s=450x450"
    },
    {
      src: "https://i.pinimg.com/736x/b4/30/78/b43078e52083c2032c4b49095d7547ce--karachi-pakistan-taj-mahal.jpg"
    },
    {
      src: "data:image/jpeg;base64,"
    },
    {
      src: "https://blogpakistan.pk/wp-content/uploads/2021/03/a-3.jpg"
    },
    {
      src: "https://zameenblog.s3.amazonaws.com/blog/wp-content/uploads/2019/05/image-6-34-1024x640.jpg"
    },
    {
      src: "data:image/jpeg;base64,"
    }
  ]
  const handleSlider=(i)=>{
    setSlideNumber(i);
    openSlider(true);
  }

  const handleSlide=(direction)=>{
    let newSlideNumber;
    if(direction=="l"){
      newSlideNumber= slideNumer == 0 ? 5:slideNumer - 1;
    }
    else{
      newSlideNumber= slideNumer == 5 ? 0:slideNumer + 1;
    }
    setSlideNumber(newSlideNumber);
  }

  return (
    <div>
      <Navbar />
      <Header type='list' />
      <div className='hotel-container'>
        {slider &&
          <div className='slider'>
             <FontAwesomeIcon icon={faCircleXmark} className="close" onClick={()=>openSlider(false)} />
             <FontAwesomeIcon icon={faCircleArrowLeft} className="arrow" onClick={()=>handleSlide("l")}/>
             <div className='slider-wrapper'>
               <img src={photos[slideNumer].src} alt="slider image" className='slider-img'/>
             </div>
             <FontAwesomeIcon icon={faCircleArrowRight} className="arrow" onClick={()=>handleSlide("r")}/>
          </div>
        }
        <div className='hotel-wrapper'>
          <button className='book-now'>Book or Reserve Now!</button>
          <h1 className='hotel-title'>Grand Hotel</h1>
          <div className='hotel-address'>
            <FontAwesomeIcon icon={faLocationDot} />
            <span>Dha 7 near 2 river</span>
          </div>
          <span className='hotel-distance'>
            Excelent location 5Km from Abdullah Shah Darbar
          </span>
          <span className='hotel-price-highlight'>
            Book a stay over $114 at this property and get a free airport taxi
          </span>
          <div className='hotel-images'>
            {photos.map((photo, index) =>
              <div className='hotel-img-wrapper'>
                <img src={photo.src} onClick={()=>handleSlider(index)} alt="hotel images" className='hotel-img' />
              </div>
            )}
          </div>
          <div className='hotel-details'>
            <div className='hotel-details-texts'>
              <h1 className='hotel-title'>Stay in the heart of pakistan</h1>
              <p className='hotel-description'>Located  5km away from Shah abdullah gazzi darbar and it is located in the dha phase 7 near 2 river and its a beautiful place of dha</p>
            </div>
            <div className='hotel-details-pice'>
              <h1>Perfect for good food enjoy</h1>
              <span>Located near the sea view and its location is beautiful</span>
              <h2>
                <b>$250</b>  (5 days)
              </h2>
              <button>Book or Reserve Now!</button>
            </div>
          </div>
        </div>
        <MailList />
        <Footer />
      </div>
    </div>
  )
}

export default Hotel